import React, { Component } from 'react';
import Select from 'react-select';

import './ChooseTheme.css';

const options = [
  { value: 'tekno', label: 'TEKNO BOOM BOOM' },
  { value: 'teenmovies', label: 'TEEN MOVIES' },
  { value: 'scarymovies', label: 'SCARY MOVIES' },
  { value: 'wildwildwest', label: 'WILD WILD WEST' },
  { value: 'geekmusics', label: 'GEEK MUSICS' }
];

//const ChooseTheme = () => <Select options={options} styles={colourStyles} />;

const colourStyles = {
  borderRadius: 10,
  content: '" "',
  display: 'block',
  marginRight: 8,
  height: 10,
  width: 30
};

export default () => (
  <Select
    options={options}
    styles={colourStyles}
    defaultValue={options}
    label="Single select"
    options={options}
    theme={theme => ({
      ...theme,
      borderRadius: 7,
      colors: {
        ...theme.colors,
        primary25: '#62F6B4',
        primary: 'black'
      }
    })}
  />
);
