import React, { Component } from 'react';
import Select from 'react-select';

const options = [
  { value: 'tekno', label: 'TEKNO BOOM BOOM' },
  { value: 'teenmovies', label: 'TEEN MOVIES' },
  { value: 'scarymovies', label: 'SCARY MOVIES' },
  { value: 'wildwildwest', label: 'WILD WILD WEST' },
  { value: 'geekmusics', label: 'GEEK MUSICS' }
];

const ChooseTheme = () => <Select options={options} />;

export const colourOptions = [
  { value: 'ocean', label: 'Ocean', color: '#00B8D9', isFixed: true },
  { value: 'blue', label: 'Blue', color: '#0052CC', isDisabled: true },
  { value: 'purple', label: 'Purple', color: '#5243AA' },
  { value: 'red', label: 'Red', color: '#FF5630', isFixed: true },
  { value: 'orange', label: 'Orange', color: '#FF8B00' },
  { value: 'yellow', label: 'Yellow', color: '#FFC400' },
  { value: 'green', label: 'Green', color: '#36B37E' },
  { value: 'forest', label: 'Forest', color: '#00875A' },
  { value: 'slate', label: 'Slate', color: '#253858' },
  { value: 'silver', label: 'Silver', color: '#666666' }
];

export default () => (
  <Select
    defaultValue={options}
    label="Single select"
    options={options}
    theme={theme => ({
      ...theme,
      borderRadius: 7,
      colors: {
        ...theme.colors,
        primary25: 'Forest',
        primary: 'black'
      }
    })}
  />
);
