import React from 'react';
import './Rules.css';

const Rules = props => {
  if (props.modal === true) {
    return (
      <div className="Rule">
        <div className="Rule-box">
          <div className="Rule-Title">RULES</div>
          <div className="Rule-text">
            &#9733; <br />
            Trouvez le titre des musiques diffusées par le perroquet ! <br />
            Rentre ton pseudo
            <br />
            &#9733; <br />
            Choisis ton thème
            <br />
            &#9733; <br />
            Tu as 30 sec
            <br />
            &#9733; <br />
            10 musiques par thème
            <br />
            &#9733; <br />
            Le score varie en fonction des bonnes et mauvaises réponses données.
            <br />
            &#9733; <br />
            C'est parti ! <br />
            &#9733; &#9733; &#9733; <br />
          </div>
        </div>
      </div>
    );
  } else {
    return <div className="divnull"></div>;
  }
};

export default Rules;
