import React from 'react';
import './Rules.css';

const Rules = props => {
  if (props.modal === true) {
    return (
      <div className="Rule">
        <div className="Rule-box">
          <div>
            <div className="Rule-Title">RULES</div>
            <div className="Rule-text">
              Trouvez le titre des musiques diffusées par le perroquet ! <br />> Rentre ton pseudo
              <br />
              Choisis ton thème
              <br />
              Tu as 30 sec
              <br />
              10 musiques par thème
              <br />
              Le score varie en fonction des bonnes et mauvaises réponses données.
              <br />
              C'est parti !
            </div>
          </div>
        </div>
      </div>
    );
  } else {
    return <div className="divnull"></div>;
  }
};

export default Rules;
